sys_script.script:
(function executeRule(current, previous /*null when async*/) {

// 	gs.info("Info level");
// 	gs.warn("Warn level");
// 	gs.error("Error level");
// 	gs.debug("Debug level");

// 	var rightNow = new GlideDateTime();
// 	rightNow.addDaysLocalTime(1);
// 	var whenNeeded = new GlideDateTime(current.u_when_needed);

// 	if (whenNeeded.before(rightNow)) {
// 		gs.addErrorMessage("You gotta give us at least a day! Your request has not been saved.");
// 		current.setAbortAction(true);
// 	}

	// Instantiate the NeedItUtils class.  Call the isDatePast method and pass 
	// the u_when_needed value.
	var niutils = new NeedItUtils();
	var isPast = niutils.isDatePast(current.u_when_needed);

	// If the isDatePast method returns true, the date is in the past.
	if(isPast == true){
		gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved.");
		current.setAbortAction(true);
	}

	var isToday = niutils.isDateToday(current.u_when_needed);
	if (isToday) {
		gs.addErrorMessage("You have to give us at least a day! Request denied!");
	}

})(current, previous);
